INSERT INTO `detail`(`id`, `name`, `rule_id`, `type`, `grade`, `accuracy`, `os`, `server`, `db_name`, `lang`, `detail_description`) VALUES (40, 'dahua_backdoor', 27526145, 'Web_Server_Bug', '高', '高', '所有操作系统', '所有WEB服务器', '所有数据库', '所有系统语言', 'CVE编号: N/A\r\r\n危害描述: 利用大华摄像头后门漏洞，攻击者可以在非管理员权限的情况下访问摄像头产品的用户数据库，提取出用户名及哈希密码。攻击者可以利用用户名与哈希密码直接登录该摄像头从而获得该摄像头的相关权限。 \r\r\n配置建议: 建议使用大华摄像头并且没有打升级补丁的用户配置该项。');
