INSERT INTO `detail`(`id`, `name`, `rule_id`, `type`, `grade`, `accuracy`, `os`, `server`, `db_name`, `lang`, `detail_description`) VALUES (45, 'apache_cgiphp_remote_code_exec', 27526140, 'Web_Server_Bug', '高', '高', '所有操作系统', 'Apache  ', '所有数据库', 'PHP  ', 'CVE编号: CVE-2012-1823\r\r\n规则说明: 当php以cgi模式运行在Apache上时，攻击者会通过-s -d等命令开关获取源码、更改web服务器上的配置为后续执行恶意代码制造条件等。\r\r\n配置建议: 当apache服务器以cgi方式运行php时，建议开启此规则\r');
