INSERT INTO `detail`(`id`, `name`, `rule_id`, `type`, `grade`, `accuracy`, `os`, `server`, `db_name`, `lang`, `detail_description`) VALUES (321, 'wp_backwpup_remote_file_inclusion', 27000843, 'Web_Plugin_Bug', '高', '高', '所有操作系统', 'IIS  Apache  Tomcat  Nginx  Lighttpd  ', 'Mysql  ', 'PHP  ', 'CVE编号：NA\r\n危害描述：WordPress BackWPup插件实现上存在输入验证漏洞，传递到wp-content/plugins/backwpup/job/wp_export_generate.php中的\"BackWPupJobTemp\"参数的输入没有正确过滤即用于包含文件，可被利用包含远程资源上的任意文件。\r\n配置建议：对于WordPress BackWPup Plugin 2.x系统且没有安装相应补丁的系统建议检查此项。');
INSERT INTO `detail`(`id`, `name`, `rule_id`, `type`, `grade`, `accuracy`, `os`, `server`, `db_name`, `lang`, `detail_description`) VALUES (322, 'phpbb_http_post_inject', 27000842, 'Web_Plugin_Bug', '高', '高', '所有操作系统', 'IIS  Apache  Tomcat  Nginx  Lighttpd  ', 'SQL Server  Mysql  Postgres  Oracle  ', 'PHP  ', 'CVE编号：NA\r\n危害描述：phpBB是一款使用多种模块扩展的流行的论坛系统。phpBB的附件模块存在一个HTTP POST注入问题，远程攻击者可以利用这个漏洞以WEB进程权限查看文件内容。由于attachment mod用户接口不充分过滤文件名数据，通过HTTP POST，注入包含多个\'../\'字符的文件名，可绕过WEB ROOT限制，以用户进程权限查看系统文件内容。\r\n配置建议：对于phpBB Group Attachment Mod 2.3.10且没有安装相应补丁的系统建议检查此项。');
INSERT INTO `detail`(`id`, `name`, `rule_id`, `type`, `grade`, `accuracy`, `os`, `server`, `db_name`, `lang`, `detail_description`) VALUES (323, 'phpbb_highlight_twince_decode', 27000841, 'Web_Plugin_Bug', '高', '高', '所有操作系统', 'IIS  Apache  Tomcat  Nginx  Lighttpd  ', 'SQL Server  Mysql  Postgres  Oracle  ', 'PHP  ', 'CVE编号：CVE-2004-1315\r\n危害描述: phpBB是一款由PHP编写的WEB论坛应用程序，支持多种数据库系统，可使用在多种Unix和Linux操作系统下。phpBB对highlight变量不正确的二次解码导致远程攻击者可以注入PHP代码执行系统命令。由于phpBB的URL解码会把%2527转换成%27(单引号)，并没有追加\'\\\'号的，因此可以注入PHP代码执行系统命令。\r\n配置建议：对于以下系统且没有安装相应补丁的系统建议检查此项。phpBB Group phpBB 2.0.9、phpBB Group phpBB 2.0.8 a\r\nphpBB Group phpBB 2.0.8、phpBB Group phpBB 2.0.7、phpBB Group phpBB 2.0.6 d、phpBB Group phpBB 2.0.6 c、phpBB Group phpBB 2.0.6、phpBB Group phpBB 2.0.5、phpBB Group phpBB 2.0.4、phpBB Group phpBB 2.0.3、phpBB Group phpBB 2.0.2、phpBB Group phpBB 2.0.10、phpBB Group phpBB 2.0.1、phpBB Group phpBB 2.0');
INSERT INTO `detail`(`id`, `name`, `rule_id`, `type`, `grade`, `accuracy`, `os`, `server`, `db_name`, `lang`, `detail_description`) VALUES (324, 'wp_mac_photo_gallery_php', 27000840, 'Web_Plugin_Bug', '高', '高', '所有操作系统', 'IIS  Apache  Tomcat  Nginx  Lighttpd  ', 'Mysql  ', 'PHP  ', 'CVE编号：NA\r\n危害描述：WordPress Mac Photo Gallery是将互联网图片库设计并整合到WordPress网站的插件。远程Web服务器上安装的Mac Photo Gallery for WordPress 2.8没有正确将用户提供的输入过滤到\'macdownload.php\'脚本的\'albid\'参数，受到文件泄露漏洞的影响，此漏洞可允许未经身份验证的远程攻击者通过目录遍历序列请求读取任意文件。\r\n配置建议：对于WordPress Mac Photo Gallery < 2.10且没有安装相应补丁的系统建议检查此项。');
INSERT INTO `detail`(`id`, `name`, `rule_id`, `type`, `grade`, `accuracy`, `os`, `server`, `db_name`, `lang`, `detail_description`) VALUES (325, 'wp_cat_dir_traversal', 27000839, 'Web_Plugin_Bug', '低', '低', '所有操作系统', 'IIS  Apache  Tomcat  Nginx  Lighttpd  ', 'Mysql  ', 'PHP  ', 'CVE编号：NA\r\n危害描述：WordPress是一款免费的论坛Blog系统。在WordPress的wp-includes/theme.php文件的get_category_template()函数中，没有正确地过滤对index.php所传送的cat参数便将其用在了template-loader.php文件中，这可能允许攻击者通过目录遍历攻击包含本地资源的任意PHP文件。成功利用这个漏洞要求将WordPress安装在了Windows平台上且攻击者拥有在受影响系统上存储PHP文件的权限。\r\n配置建议：对于WordPress WordPress 2.3.3且没有安装相应补丁的系统建议检查此项。');
INSERT INTO `detail`(`id`, `name`, `rule_id`, `type`, `grade`, `accuracy`, `os`, `server`, `db_name`, `lang`, `detail_description`) VALUES (326, 'wp_gallery_getconfig_php', 27000838, 'Web_Plugin_Bug', '高', '高', '所有操作系统', 'IIS  Apache  Tomcat  Nginx  Lighttpd  ', 'Mysql  ', 'PHP  ', 'CVE编号：CVE-2008-5752\r\n危害描述：Page Flip Image Gallery是Wordpress论坛中使用的用于放映相册的插件。Page Flip Image Gallery插件的books/getConfig.php文件中没有正确地过滤用户所提供的book_id参数，远程攻击者可以通过提交恶意请求执行目录遍历攻击，读取任意系统文件。成功攻击要求打开了magic_quotes_gpc。\r\n配置建议：对于安装了Flipping Book Wordpress Team Page Flip Image Gallery 0.2.2且没有安装相应补丁的系统建议增加此选项。');
INSERT INTO `detail`(`id`, `name`, `rule_id`, `type`, `grade`, `accuracy`, `os`, `server`, `db_name`, `lang`, `detail_description`) VALUES (327, 'wp_dm_albums_del', 27000837, 'Web_Plugin_Bug', '高', '高', '所有操作系统', 'IIS  Apache  Tomcat  Nginx  Lighttpd  ', 'Mysql  ', 'PHP  ', 'CVE编号：NA\r\n危害描述：DM Albums是在WordPress论坛中所使用的相册插件。DM Albums插件中存在多个输入验证和访问验证错误，远程攻击者可以绕过安全限制或执行跨站脚本攻击。 \r\n配置建议：对于安装DutchMonkey DM Albums 2.1以及DutchMonkey DM Albums 2.0且没有安装相应补丁的系统建议检查此项。');
INSERT INTO `detail`(`id`, `name`, `rule_id`, `type`, `grade`, `accuracy`, `os`, `server`, `db_name`, `lang`, `detail_description`) VALUES (328, 'wp_admin_file_php', 27000836, 'Web_Plugin_Bug', '中', '中', '所有操作系统', 'IIS  Apache  Tomcat  Nginx  Lighttpd  ', 'Mysql  ', 'PHP  ', 'CVE编号：CVE-2009-3890\r\n危害描述：WordPress过滤过程没有考虑到带有多个扩展名的文件，用户可以上传带有.php.jpg扩展名的任意PHP脚本，并通过直接请求上传的文件来执行恶意脚本。\r\n配置建议：对于WordPress WordPress <= 2.8.5且没有安装相应补丁的版本建议检查此项。');
INSERT INTO `detail`(`id`, `name`, `rule_id`, `type`, `grade`, `accuracy`, `os`, `server`, `db_name`, `lang`, `detail_description`) VALUES (329, 'wp_uploadimage_php', 27000835, 'Web_Plugin_Bug', '高', '高', '所有操作系统', 'IIS  Apache  Tomcat  Nginx  Lighttpd  ', 'Mysql  ', 'PHP  ', 'CVE编号：NA\r\n危害描述：WordPress是一款免费的论坛Blog系统。WordPress Recip.ly在实现上存在安全漏洞，远程攻击者可利用此漏洞上传任意文件并在网络服务器进程中运行，导致未授权访问或权限提升。wp-content/plugins/reciply/uploadImage.php没有正确地验证上传的文件类型，导致通过上传的PHP文件执行任意PHP代码。\r\n配置建议：对于WordPress Recip.ly 1.x且没有安装相应补丁的系统建议增加此项检查。');
INSERT INTO `detail`(`id`, `name`, `rule_id`, `type`, `grade`, `accuracy`, `os`, `server`, `db_name`, `lang`, `detail_description`) VALUES (330, 'wp_avatar_src_upload', 27000834, 'Web_Plugin_Bug', '高', '高', '所有操作系统', 'IIS  Apache  Tomcat  Nginx  Lighttpd  ', 'Mysql  ', 'PHP  ', 'CVE编号：NA\r\r\n危害描述：WordPress User Avatar插件是富文本编辑器。User Avatar plugin for WordPress在实现上存在安全漏洞，恶意用户可利用此漏洞控制受影响系统。通过wp-content/plugins/user-avatar/user-avatar-pic.php中的\"src\"参数发送的输入没有正确过滤就用于缓存文件，可被利用上传和执行任意PHP文件。\r\r\n配置建议：对于WordPress Eventify plugin 1.x版本且没有打过相应补丁的建议检查此项。');
