INSERT INTO `detail`(`id`, `name`, `rule_id`, `type`, `grade`, `accuracy`, `os`, `server`, `db_name`, `lang`, `detail_description`) VALUES (764, 'chrome_pdf_information_disclosure', 21757993, 'Content_filter', '中', '中', '所有操作系统', '所有WEB服务器', '所有数据库', 'Python  ', '规则说明：在Google Chrome浏览器使用的PDF JavaScript API中存在安全漏洞，攻击者只需在PDF中加入一条特定API调用，即可导致用户的Chrome浏览器将个人信息发送至攻击者指定的位置。\r\n攻击危害：攻击者可利用该漏洞进行攻击前期的信息搜集，从而确定Web文件路径、计算机用户名称、主机名称等信息，以开展下一步有针对性的攻击。\r\n配置建议: 建议存在PDF文档的站点作为常规配置。');
